sample = ""

#________________________________________________________________________________
# METODO 1

def count(DNA):
    A = 0
    C = 0
    G = 0
    T = 0
    for i in DNA:
        if i == "A":
            A = A + 1
        elif i == "C":
            C = C + 1
        elif i == "G":
            G = G + 1
        elif i == "T":
            T = T + 1

    return A, C, G, T

a, c, g, t = count(sample)
print(f"{a} {c} {g} {t}")



