# METODO 2
def count2(DNA):
    A = DNA.count("A")
    C = DNA.count("C")
    G = DNA.count("G")
    T = DNA.count("T")
    return A, C, G, T

a2, c2, g2, t2 = count2(sample)
print(f"{a2} {c2} {g2} {t2}")


#________________________________________________________________________________
# METODO 3
def count3(DNA):
    bases = {
        "A": DNA.count("A"),
        "C": DNA.count("C"),
        "G": DNA.count("G"),
        "T": DNA.count("T")
    }
    return bases

dna_bases = count3(sample)
print(f"{dna_bases["A"]} {dna_bases["C"]} {dna_bases["G"]} {dna_bases["T"]}")